// App.js
import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import QuizFinishedModal from './components/QuizFinishedModal';
import Footer from "./components/Footer";
import Header from "./components/Header";
import QuestionComponent from "./components/QuestionComponent";
import {Route, Router} from "react-router-dom";
import {NavLink} from "react-bootstrap";

function App() {
    const [isQuizFinished, setIsQuizFinished] = useState(false);

    const handleQuizFinish = () => {
        setIsQuizFinished(true);
    };

    const handleCloseModal = () => {
        setIsQuizFinished(false);
    };

    return (

        <Router>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container-fluid">
                    <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                        <li className="nav-item">
                            <NavLink className="nav-link" activeClassName="active" to="/profile">Profile</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink className="nav-link" activeClassName="active" to="/login">Log in</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink className="nav-link" activeClassName="active" to="/logout">Log out</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink className="nav-link" activeClassName="active" to="/about">About</NavLink>
                        </li>
                    </ul>
                </div>
            </nav>
            <div className="container mt-4">
                <Route path="/profile" component={Footer} />
                <Route path="/login" component={Header} />
                {/*<Route path="/logout" component={Logout} />
                <Route path="/about" component={About} />*/}
            </div>
        </Router>
       /* <div className="bg-body-tertiary flex-column min-vh-100">
           <Header/>
            <main className="container mx-auto my-4">
                <QuestionComponent onQuizFinish={handleQuizFinish} />
            </main>
           <Footer/>
            {isQuizFinished && <QuizFinishedModal onClose={handleCloseModal} />}
        </div>*/
    );
}

export default App;
