import React, { useState, useEffect } from 'react';
import axios from 'axios';

function QuestionComponent() {
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [selectedOption, setSelectedOption] = useState(null);

    useEffect(() => {
        axios.get('/api/questions')
            .then(response => {
                setQuestions(response.data);
            })
            .catch(error => {
                console.error('Error fetching questions:', error);
            });
    }, []);

    const handleOptionSelect = (option) => {
        setSelectedOption(option);
    };

    const handleNextClick = () => {
        if (selectedOption !== null) {
            setCurrentQuestionIndex(prevIndex => prevIndex + 1);
            setSelectedOption(null);
        } else {
            alert('Please select an option.');
        }
    };

    return (
        <div>
            {currentQuestionIndex < questions.length ? (
                <div className="card mb-3">
                    <div className="card-header">{questions[currentQuestionIndex].question}</div>
                    <div className="card-body">
                        <form>
                            {JSON.parse(questions[currentQuestionIndex].options).map((option, index) => (
                                <div className="form-check" key={index}>
                                    <input className="form-check-input" type="radio" name="question" id={`option-${index}`} value={option} onChange={() => handleOptionSelect(option)} checked={selectedOption === option} />
                                    <label className="form-check-label" htmlFor={`option-${index}`}>{option}</label>
                                </div>
                            ))}
                        </form>
                        <button className="btn btn-primary mt-3" onClick={handleNextClick}>Next</button>
                    </div>
                </div>
            ) : (
                <div>No more questions.</div>
            )}
        </div>
    );
}

export default QuestionComponent;
