import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import QuizFinishedModal from "./components/QuizFinishedModal";
import QuestionComponent from "./components/QuestionComponent";



function App() {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [isQuizFinished, setIsQuizFinished] = useState(false);

    const handleNext = () => {
        if (currentQuestionIndex < quiz.questions.length - 1) {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
        } else {
            setIsQuizFinished(true);
        }
    };

    const handlePrevious = () => {
        if (currentQuestionIndex > 0) {
            setCurrentQuestionIndex(currentQuestionIndex - 1);
        }
    };

    const handleCloseModal = () => {
        setIsQuizFinished(false);
        setCurrentQuestionIndex(0); // Скидання стану для повторного проходження тесту
    };

    const question = quiz.questions[currentQuestionIndex];

    return (
        <div className="bg-body-tertiary flex-column min-vh-100">
            <header className="navbar navbar-expand-lg navbar-light bg-success-subtle text-info-emphasis p-4">
                <div className="container-fluid">
                    <a className="navbar-brand" href="#">
                        Mental
                    </a>
                </div>
            </header>
            <main className="container mx-auto my-4">
                <section id="working-page" className="mb-8">
                    <h2 className="text-uppercase text-center text-success">Quiz</h2>
                    <div className="container mt-5">
                        <QuestionComponent question={question.text} options={question.options} />
                    </div>
                    <div className="d-flex justify-content-between mt-4">
                        <button className="btn btn-secondary" onClick={handlePrevious}>Previous</button>
                        <button className="btn btn-secondary" onClick={handleNext}>{currentQuestionIndex === quiz.questions.length - 1 ? 'Finish' : 'Next'}</button>
                    </div>
                </section>
            </main>
            <footer className="position-absolute bottom-0 min-vw-100 bg-success-subtle text-success p-4">
                <div className="container mx-auto">
                    <p>&copy; 2024 Mental</p>
                </div>
            </footer>
            {isQuizFinished && <QuizFinishedModal onClose={handleCloseModal} />}
        </div>
    );
}


export default App;
