// components/Quiz.js
import React, { useState } from 'react';
import QuestionComponent from './QuestionComponent';
import QuizFinishedModal from './QuizFinishedModal';

const questionsData = [
    // ваші дані для питань
];

function Quiz({ onQuizFinish }) {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

    const handleNext = () => {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
    };

    const handlePrevious = () => {
        setCurrentQuestionIndex(currentQuestionIndex - 1);
    };

    const handleFinish = () => {
        onQuizFinish();
    };

    const question = questionsData[currentQuestionIndex];

    return (
        <section id="working-page" className="mb-8">
            <h2 className="text-uppercase text-center text-success">Quiz</h2>
            <div className="container mt-5">
                <QuestionComponent question={question.question} options={question.options} />
            </div>
            <div className="d-flex justify-content-between mt-4">
                <button className="btn btn-secondary" onClick={handlePrevious} disabled={currentQuestionIndex === 0}>Previous</button>
                <button className="btn btn-secondary" onClick={currentQuestionIndex === questionsData.length - 1 ? handleFinish : handleNext}>
                    {currentQuestionIndex === questionsData.length - 1 ? 'Finish' : 'Next'}
                </button>
            </div>
        </section>
    );
}

export default Quiz;
